using PX.Data;
using PX.Objects.SO;
using System.Collections;
using System.Collections.Generic;

namespace ISExtendSOOrderToAllowSidePanelTiedToSOLine
{
    public class ISSOOrderEntry_Extension : PXGraphExtension<SOOrderEntry>
    {
        //
        // The magic of this customization comes from the addition two JavaScript scripts on SO301000
        // See also: https://stackoverflow.com/questions/62344790/acumatica-side-panel-linked-to-detail-item
        //
        public static bool IsActive() => true;

        public PXAction<SOOrder> SidePanelCallback;
        [PXButton]
        [PXUIField(DisplayName = "SidePanelCallback", Visible = false)]
        public virtual IEnumerable sidePanelCallback(PXAdapter adapter)
        {
            SOOrder doc = Base.Document.Current;
            if (doc == null) return adapter.Get();

            var docExt = doc.GetExtension<ISSOOrderExt>();
            docExt.UsrSidePanelDetail = adapter.CommandArguments;

            return adapter.Get();
        }
        
        public PXAction<SOOrder> SidePanelRefresh;
        [PXButton]
        [PXUIField(DisplayName = "SidePanelRefresh", Visible = false)]
        public virtual IEnumerable sidePanelRefresh(PXAdapter adapter)
        {
            SOOrder doc = Base.Document.Current;
            if (doc is null) return adapter.Get();

            Dictionary<string, string> parameters = new Dictionary<string, string>();
            string[] args = adapter.CommandArguments.Split('*');
            switch (doc.GetExtension<ISSOOrderExt>()?.UsrSidePanelDetail)
            {
                case "BOM_MOH":
                    parameters.Add("InventoryID", args[0]);
                    parameters.Add("Amount", args[1]);
                    throw new PXRedirectToGIRequiredException("JM_BOM Materials On Hand", parameters) { Mode = PXBaseRedirectException.WindowMode.Layer };

                case "Kit_MOH":
                    parameters.Add("InventoryID", args[0]);
                    parameters.Add("Amount", args[1]);
                    throw new PXRedirectToGIRequiredException("KitMaterialOnHand", parameters) { Mode = PXBaseRedirectException.WindowMode.Layer };
            }

            return adapter.Get();
        }
    }
}

using PX.Data;

namespace ISExtendSOOrderToAllowSidePanelTiedToSOLine
{
    public class ISSOOrderExt : PXCacheExtension<PX.Objects.SO.SOOrder>
    {
        public static bool IsActive() => true;

        #region UsrSidePanelDetail
        [PXString]
        [PXUIField(DisplayName = "Side Panel Detail", Enabled = false)]
        public virtual string UsrSidePanelDetail { get; set; }
        public abstract class usrSidePanelDetail : PX.Data.BQL.BqlString.Field<usrSidePanelDetail> { }
        #endregion
    }
}
